mapper.insert:
insert into
	board
values
(
		board_seq.nextval
		, #{userid}
		, #{title}
		, #{content}
		, sysdate
		, #{hit}


			, #{originalFileName}
			, #{savedFileName}


			,null
			,null




)

mapper.update:
update
	board
set
	title = #{title}
	,content = #{content}


		,originalfilename = #{originalFileName}
		,savedfilename = #{savedFileName}


where
	boardnum = #{boardnum}
	and userid = #{userid}

mapper.delete:
delete 
	board
where
	boardnum = #{boardnum}
	and userid = #{userid}
